const defaultAuthorFotoBase64 = "data:image/jpeg;base64,";
const defaultBookCoverBase64 = "data:image/jpeg;base64,";


export function getDefaultAuthorFoto() {
  return getBlobFromImageUrl(defaultAuthorFotoBase64);
}

export function getDefaultBookCover() {
  return getBlobFromImageUrl(defaultBookCoverBase64);
}

function getBlobFromImageUrl(imageUrl: string) {
  var byteString = atob(imageUrl.split(',')[1]);

  // separate out the mime component
  var mimeString = imageUrl.split(',')[0].split(':')[1].split(';')[0];

  // write the bytes of the string to an ArrayBuffer
  var arrayBuffer = new ArrayBuffer(byteString.length);
  var _ia = new Uint8Array(arrayBuffer);
  for (var i = 0; i < byteString.length; i++) {
    _ia[i] = byteString.charCodeAt(i);
  }

  var dataView = new DataView(arrayBuffer);
  return new Blob([dataView], {type: mimeString});
}
